mapper.insert:
insert into ddwb_material_act (material_name, company, pre_unit_price, 
  pre_unit_count, pre_cal, act_cal, 
  act_unit_price, act_unit_count, order_id
  )
values (#{materialName,jdbcType=VARCHAR}, #{company,jdbcType=VARCHAR}, #{preUnitPrice,jdbcType=DECIMAL}, 
  #{preUnitCount,jdbcType=DECIMAL}, #{preCal,jdbcType=DECIMAL}, #{actCal,jdbcType=DECIMAL}, 
  #{actUnitPrice,jdbcType=DECIMAL}, #{actUnitCount,jdbcType=DECIMAL}, #{orderId,jdbcType=INTEGER}
  )

mapper.select:
SELECT a.material_name,a.company,pre_unit_price,SUM(pre_unit_count) AS pre_unit_count,SUM(pre_cal) AS pre_cal FROM ddwb_material_act a INNER JOIN ddwb_work_order b ON a.order_id = b.id INNER JOIN ddwb_crop_batch c ON b.crop_batch_id = c.id WHERE c.id IN
#{batchId}
AND b.farming_id = #{farmingId,jdbcType=INTEGER} GROUP BY b.farming_id,a.material_name
